import React, { useRef, useState } from 'react'
import { Container, Form, Button } from 'react-bootstrap'
import { useAuth } from '../contexts/AuthContext';

export default function SignIn() {
    const email = useRef();
    const pWord = useRef();
    const confPWord = useRef();
    const {signIn} = useAuth();
    const {error, setError} = useState('');
    const [loading, setLoading] = useState(false);

    async function handlesubmit(e) {
        e.preventDefault();
        if (pWord.current.value !== confPWord.current.value) return setError('Passwords don\'t match');
        
        try {
            setError('');
            setLoading(true);
            await signIn(email.current.value, pWord.current.value);    
        } catch {
            setError('Couldn\'t SignIn user');
        }
        setLoading(false);
        
    }

    return (
    <>
        <Container>
            <h2 className='w-100 text-center mb-4'>Sign In</h2>
            <Form>
                <Form.Group id='email'>
                    <Form.Label>Email</Form.Label>
                    <Form.Control type='email' ref={email} required />
                </Form.Group>
                <Form.Group id='password'>
                    <Form.Label>Password</Form.Label>
                    <Form.Control type='password' ref={pWord} required />
                </Form.Group>
                <Form.Group id='confPassword'>
                    <Form.Label>Confirm Password</Form.Label>
                    <Form.Control type='password' ref={confPWord} required />
                </Form.Group>
                <div className='mb-4'>
                    <br />
                </div>
                <Button className='w-100' type='submit'>
                    SignIn
                </Button>  
            </Form>
            <div className='w-100 text-center mb-2'>
                Log In
            </div>
        </Container>
    </>    
    );
}
