import React, { useRef, useState } from 'react'
import { Container, Form, Button, Alert} from 'react-bootstrap'
import { useAuth } from '../contexts/AuthContext';

export default function LogIn() {
    const email = useRef();
    const pWord = useRef();
    const { signIn } = useAuth();
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);


    async function handelSubmit(e) {
        e.preventDefault();
        
        
        try {
            setError('');
            setLoading(true);
            await signIn(email.current.value, pWord.current.value);    
        } catch (error){
            console.log(error);
            //setError('Couldn\'t SignIn user');
        }
        setLoading(false);  
    }

    return (
        <>
            <Container>
                <h2 className='w-100 text-center mb-4'>Log In</h2>
                {error && <Alert variant='danger'>{error}</Alert>}
                <Form onSubmit={handelSubmit}>
                    <Form.Group id='email'>
                        <Form.Label>Email</Form.Label>
                        <Form.Control type='email' ref={email} required />
                    </Form.Group>
                    <Form.Group id='password'>
                        <Form.Label>Password</Form.Label>
                        <Form.Control type='password' ref={pWord} required />
                    </Form.Group>
                    
                    <Button className='w-100' type='submit'disabled={loading}>
                        LogIn
                    </Button>  
                </Form>
                <div className='w-100 text-center mb-2'>
                    SignUp <Link to='/'>SignUP</Link>
                </div>
            </Container>
        </>    
        );
}
